mapper.select:
SELECT course.ID, course.KIND_NAME, course.TITLE, course.INTRODUCTION, course.CREATED_TIME, course.COURSE_LIKE, course.COURSE_STAR, course.COURSE_HEAD_URL, user.ID USER_ID, user.NICKNAME USER_NICKNAME, user.HEAD_URL USER_HEAD_URL FROM ( SELECT course.ID ID, course.KIND_NAME KIND_NAME, course.TITLE TITLE, course.INTRODUCTION INTRODUCTION, course.CREATED_TIME CREATED_TIME, course.LIKE COURSE_LIKE, course.STAR COURSE_STAR, course.HEAD_URL COURSE_HEAD_URL, course.USER_ID FROM course WHERE course.ID <= (SELECT ID FROM course
where KIND_NAME = #{kindName}
ORDER BY ID DESC limit #{offset},1)
and KIND_NAME = #{kindName}
ORDER BY ID DESC LIMIT 10) course inner JOIN user ON course.USER_ID = user.ID

select count(1)
from course_comment
where COURSE_ID = #{courseId}

select count(1)
from course_section_comment
where SECTION_ID = #{sectionId}

mapper.update:
update course
set KIND_NAME    = #{kindName},
    TITLE        = #{title},
    INTRODUCTION = #{introduction},
    HEAD_URL     = #{headUrl}
where ID = #{id}
  and USER_ID = #{userId}

update course_chapter
set TITLE = #{title}
where ID = #{id}
  and COURSE_ID = #{courseId}

update course_section
set TITLE        = #{title},
    INTRODUCTION = #{introduction},
    VIDEO_URL    = #{videoUrl}
where ID = #{id}
  and CHAPTER_ID = #{courseId}
