mapper.select:
select course.ID, course.KIND_NAME, course.TITLE, course.INTRODUCTION, course.CREATED_TIME, course.LIKE, course.STAR, course.HEAD_URL COURSE_HEAD_URL, user.NICKNAME USER_NICKNAME, user.HEAD_URL USER_HEAD_URL from course left join user on course.USER_ID=user.ID where course.ID >= (select ID from course
where KIND_NAME = #{kindName}
limit #{offset},1) limit #{pageSize}

select ID, COURSE_ID, TITLE
from course_chapter
where COURSE_ID = #{courseId}

mapper.update:
update course

    KIND_NAME = #{kindName},
    TITLE = #{title},
    INTRODUCTION = #{introduction}
    HEAD_URL = #{headUrl}

where ID = #{id} and userId = #{userId}

update course_chapter
set TITLE = #{title}
where ID = #{id}
  and COURSE_ID = #{courseId}
