mapper.select:
select course.ID, course.KIND_NAME, course.TITLE, course.INTRODUCTION, course.CREATED_TIME, course.LIKE, course.STAR, course.HEAD_URL COURSE_HEAD_URL, user.ID USER_ID, user.NICKNAME USER_NICKNAME, user.HEAD_URL USER_HEAD_URL from course left join user on course.USER_ID = user.ID where course.ID <= (select ID from course
where KIND_NAME = #{kindName}
order by ID desc limit #{offset},1)
and KIND_NAME = #{kindName}
order by ID desc limit #{pageSize}

select count(1)
from course_comment
where COURSE_ID = #{courseId}

select count(1)
from course_section_comment
where SECTION_ID = #{sectionId}

mapper.update:
update course

    KIND_NAME = #{kindName},
    TITLE = #{title},
    INTRODUCTION = #{introduction}
    HEAD_URL = #{headUrl}

where ID = #{id} and userId = #{userId}

update course_chapter
set TITLE = #{title}
where ID = #{id}
  and COURSE_ID = #{courseId}
